import React from "react";
import lisaProfilePic from "../assets/lisaProfilePic.jpg";
import { useState, useEffect } from "react";

function Header() {
  const [searchValue, setSearchValue] = useState();

  let searchBar = document.querySelector(".searchBar");
  searchBar.addEventListener("search", function (event) {
    setSearchValue(event.target.value);
  });

  useEffect(() => {
    async function apiCall() {
      const apiResponse = await get10Movies(TRENDING_API_URL);
      setTrendingMovieAPI(apiResponse);
    }
    async function apiCall2() {
      const apiResponse3 = await get10Movies(REVENUE_API_URL);
      setRevenueMovieAPI(apiResponse3);
    }
    async function apiCall3() {
      const apiResponse3 = await get10Movies(RATINGS_API_URL);
      setRatingMovieAPI(apiResponse3);
    }
    apiCall();
    apiCall2();
    apiCall3();
  }, []);

  return (
    <nav className="navbar flex w-screen   p-2 pl-10 pr-10 shadow-md gap-10 bg-amber-200 justify-start items-center">
      <ul className="flex gap-5   font-bold items-center">
        <li className="pr-10 text-3xl font-black">VandyBox</li>
        <li className="">
          <a href="">Home</a>
        </li>
        <li className="">
          <a href="">Favorites</a>
        </li>
        <li className="">
          <a href="">Categories</a>
        </li>
        <li className="">
          <a href="">Rankings</a>
        </li>
      </ul>
      <input
        type="search"
        className="bg-stone-10 border border-gray-300 text-gray-900 w-1/3 text-sm rounded-lg block p-2.5 searchBar"
        placeholder="Search..."
        defaultValue={""}
        onsearch={""}
      />
      <a className="ml-auto" href="">
        <img
          src={lisaProfilePic}
          className="object-cover rounded-full w-16 h-16 "
        />
      </a>
    </nav>
  );
}

export default Header;
