import React from "react";
import { useState, useEffect } from "react";

const IMG_PATH = "https://image.tmdb.org/t/p/w500";
const SEARCH_API_URL =
  'https://api.themoviedb.org/3/search/movie?api_key=75e05708188d5f5a0a191495cf4a48db&language=en-US&page=1&include_adult=false&query="';

async function get1Movie(url) {
  const res = await fetch(url);
  const data = await res.json();
  let movieObjectComplicated = getFirst(data.results)[0];
  return returnMovieObjectSimple(movieObjectComplicated);
}

function getFirst(array) {
  return array.slice(0, 1);
}

function returnMovieObjectSimple(movies) {
    function addMoviesToArray(movies) {
        let array = [];
        movies.forEach((movie) => {
          const { title, poster_path, overview, release_date, vote_average } = movie;
            array.push({
              title: title,
              image: `${IMG_PATH}${poster_path}`,
              overview: overview,
              release_date: release_date,
              vote_average: vote_average
            });
        });
        return array;
      }
}

function UserInfoGrid({ userData, selectedUserInfo }) {
//   useEffect(() => {
//     async function apiCall5() {
//       const apiResponse = await get10Movies(TRENDING_API_URL);
//       setTrendingMovieAPI(apiResponse);
//     }
//     apiCall5();
//   }, [apiCall5]);

  return (
    <div className="grid grid-cols-4 gap-2">
      {selectedUserInfo == "Fav Movies" &&
        userData.favorites.map((movieName) => (
          <div className="flex justify-center items-center h-80">
            <span className="h-full w-64 flex justify-center items-center border border-yellow-500">
              <span className="text-lg font-bold italic">{movieName}</span>
            </span>
          </div>
        ))}
      {selectedUserInfo == "To Watch" &&
        userData.toWatch.map((movieName) => (
          <div className="flex justify-center items-center h-80">
            <span className="h-full w-64 flex justify-center items-center border border-yellow-500">
              <span className="text-lg font-bold italic">{movieName}</span>
            </span>
          </div>
        ))}
      {selectedUserInfo == "Friends" &&
        userData.friends.map((friend) => (
          <div className="flex justify-center items-center h-80">
            <span className="h-full w-64 flex justify-center items-center border border-yellow-500">
              <span className="text-lg font-bold italic">{friend}</span>
            </span>
          </div>
        ))}
      {selectedUserInfo == "Watch Groups" &&
        userData.watchGroups.map((group) => (
          <div className="flex justify-center items-center h-80">
            <span className="h-full w-64 flex justify-center items-center border border-yellow-500">
              <span className="text-lg font-bold italic">{group}</span>
            </span>
          </div>
        ))}
    </div>
  );
}

export default UserInfoGrid;
